import React,{useContext, useEffect, useState} from "react";
import { UserContext } from "../../App";

const Profile = ()=>{
    const [mypics,setPics] = useState([])
    const user = JSON.parse(localStorage.getItem("user"))
    const [image, setImage] = useState("");
    const [url, setUrl] = useState(undefined);
    useEffect(()=>{
        fetch('/mypost',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        }).then(res=>res.json())
        .then(result=>{
            setPics(result.mypost)
        })
    },[])
    const updatePhoto = ()=>{
        const data = new FormData()
        data.append("file",image)
        data.append("upload_preset","insta-clone")
        data.append("cloud_name","dhw1mueq4")
        fetch("https://api.cloudinary.com/v1_1/dhw1mueq4/image/upload",{
            method:'POST',
            body:data
        })
        .then((res)=>res.json())
        .then(data=>{
            setUrl(data.url)
        })
        .catch(err=>{
            console.log(err)
        })
    }
    return(
        <div style={{maxWidth:"550px",margin:"0px auto"}}>
            <div style={{
                margin:"18px 0px",
                borderBottom:"1px solid grey"
            }}>
            <div style={{
                display: 'flex',
                justifyContent:"space-around"
            }}>
                <div>
                    <img style={{width:"160px",height:"160px",borderRadius:"80px"}}
                    src={user?user.pic:"loading"} />
                </div>
                <div>
                 <h4>{user.name}</h4>
                 <h5>{user.email}</h5>
                    <div style={{display:"flex",justifyContent:"space-between",width:"108%"}}>
                        <h5>{mypics.length} Posts</h5>
                        <h5>{user.followers.length}followers</h5>
                        <h5>{user.following.length}following</h5>
                    </div>
                </div>

            </div>
            <button
          className="btn waves-effect waves-light #64b5f6 blue darken-1"
          style={{margin:"10px 0px 10px 52px"}}
          onClick={()=>{
            updatePhoto()
          }}
        >
          Update pic
        </button>
        <div className="file-field input-field">
                <div className="btn #64b5f6 blue darken-1">
                    <span>Upload image</span>
                    <input type="file" onChange={(e)=>setImage(e.target.files[0])}/>
                </div>
                <div className="file-path-wrapper">
                    <input className="file-path validate" type="text" />
                </div>
            </div>
            </div>

            <div className="gallery">
                {
                    mypics.map(item=>{
                        return(
                            <img key={item._id} className="item" src={item.photo} alt={item.title}/> 
                        )
                    })
                }



            </div>
        </div>
    )
}

export default Profile;
