import React, { useEffect, useState } from "react";

const Home = ()=>{
    const [data,setData] = useState([])
    useEffect(()=>{
        fetch('/allpost',{
            headers:{
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            }
        }).then(res=>res.json())
        .then(result=>{
            setData(result.posts)
        })
    },[])
    const likepost = (id)=>{
        fetch("/like",{
            method:'put',
            headers:{
                "Authorization":"Bearer "+ localStorage.getItem('jwt'),
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                postId:id
            })
        }).then(res=>res.json())
        .then(result=>{
            const newData = data.map(item=>{
                if(item._id===result.id){
                    return result
                }else{
                    return item;
                }
            })
            setData(newData)
        })
    }
    const unlikepost = (id)=>{
        fetch("/unlike",{
            method:'put',
            headers:{
                "Authorization":"Bearer "+ localStorage.getItem('jwt'),
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                postId:id
            })
        }).then(res=>res.json())
        .then(result=>{
            //console.log(result);
            const newData = data.map(item=>{
                if(item._id===result.id){
                    return result
                }else{
                    return item;
                }
            })
            setData(newData)
        })
    }
    return(
        <div className="home">
            {
                data.map(item=>{
                    return (
                        <div className="card home-card" key={item._id}>
                        <h5>{item.postedBy.name}</h5>
                        <div className="card-image"> 
                            <img src={item.photo} />
                        </div>
                        <div className="card-content">
                        <i className="material-icons" style={{color:"red"}}>favorite</i>

                        <button className="btn waves-effect waves-light #64b5f6 blue darken-1" onClick={()=>{likepost(item._id)}}>
                            Like
                        </button>
                        <button className="btn waves-effect waves-light #64b5f6 blue darken-1" onClick={()=>{unlikepost(item._id)}}>
                            Like
                        </button>
                        <h6>{item.likes.length} likes</h6>
                            <h6>{item.title}</h6>
                            <p>{item.body}</p>
                            <input type="text" placeholder="add a comment"/>
                        </div>
                    </div>
                    )
                })
            }


        </div>
    )}

export default Home;
